import 'package:flutter/cupertino.dart';
import 'package:flutter/material.dart';
import 'package:flutter/rendering.dart';
import 'package:flutter/widgets.dart';
import 'package:flutter_osm_plugin/flutter_osm_plugin.dart';
import 'package:taxi_driver/common/color_extension.dart';
import 'package:taxi_driver/view/home/run_ride_view.dart';



class TipDetailView extends StatefulWidget {
  const TipDetailView({super.key});


  @override
  State<TipDetailView> createState() => _TipDetailViewState();

}
class _TipDetailViewState extends State<TipDetailView> with OSMMixinObserver{
  bool isOpen = true;

  late MapController controller;

  @override
void initState () {
  super.initState();
  controller = MapController(
                             initPosition: GeoPoint(
        latitude: 28.0398257,
        longitude: 82.4838780,
      ),

            );

            controller.addObserver(this);
}
            @override
  void dispose() {
    // TODO: implement dispose
    super.dispose();
    controller.dispose();
  }

  @override
Widget build(BuildContext context) {

  return Scaffold(
    body: Column(
      crossAxisAlignment: CrossAxisAlignment.start,
      children: [


      SizedBox(
        width: context.width,
        height: context.width * 0.7,
        child: OSMFlutter( 
          controller: controller,
          osmOption: OSMOption(
            enableRotationByGesture: true,

              zoomOption: const ZoomOption(
                    initZoom: 8,
                    minZoomLevel: 3,
                    maxZoomLevel: 19,
                    stepZoom: 1.0,
              ),
                staticPoints: [],
              roadConfiguration:const RoadOption(
                      roadColor: Colors.blueAccent,
              ),
             userLocationMarker: UserLocationMaker (
                  personMarker: const MarkerIcon(
                      icon: Icon(
                          Icons.location_history_rounded,
                          color: Colors.red,
                          size: 48,
                      ),
                  ),
                  directionArrowMarker: const MarkerIcon(
                      icon: Icon(
                          Icons.double_arrow,
                          size: 48,
                      ),
                  ),
              ),

              showDefaultInfoWindow: false
          ),
          onMapIsReady: (isReady) {
            if(isReady) {
              print("map is ready");
            }
          },
          onLocationChanged: (myLocation) {
             print("user location :$myLocation");
          },  

          onGeoPointClicked: (myLocation) {
             print("GeoPointClicked location :$myLocation");
          },  
            ),
      ),


        Container(
          padding: const EdgeInsets.symmetric(vertical: 15),

           decoration: const BoxDecoration(
                  color: Colors.white,
                  borderRadius: BorderRadius.only(
                  topLeft: Radius.circular(10),
                  topRight: Radius.circular(10), ),
                  boxShadow:  [
                  BoxShadow(
                    color: Colors.black12,
                    blurRadius: 10,
                    offset: Offset (0,-5),
                     ),
                 ]

                  ),
          child: Column(
            children: [


             const SizedBox(
              height: 15,
             ),




             Padding(
                       padding: const EdgeInsets.symmetric(horizontal: 20, vertical: 10),
                       child:
                        Row(
                        children: [
                       Container(
                        width: 10,
                        height: 10,
                        decoration: BoxDecoration(
                          color: TColor.primary,

                        ),
                       ),

                       const SizedBox(
                        width: 15,),

                        Expanded(
                          child: Text(
                                "Moon Light Drug Distributers, Ghorahi Nepal",
                                style: TextStyle(
                                  color: TColor.primaryText,
                                  fontSize: 15,

                                ),
                                ),
                        ),

                        ],

                     ),

            ),

             const SizedBox(
              height: 15,
            ),

             InkWell(
              onTap: () {
             context.push( const RunRideView() );
              },
               child: Container(
                          width: double.maxFinite,
                          margin: const EdgeInsets.symmetric(horizontal: 20),
                          padding: const EdgeInsets.all(6),
                          decoration: BoxDecoration(
                            color: TColor.primary,
                          borderRadius: BorderRadius.circular(30),
                          ),
                          child: Stack(
                            alignment: Alignment.centerRight,
                            children: [
                            Row(
                              mainAxisAlignment: MainAxisAlignment.center,
                              children: [
                          Text(
                                "TAP TO ACCEPT", 
                                style: TextStyle(
                                  color: TColor.primaryTextW,
                                  fontSize: 17,
                                  fontWeight: FontWeight.w700,

                                ),
                                ),
                              ],
                            ),

                            Container(
                          height: 40,
                          width: 40,
                          decoration: BoxDecoration(
                            color: Colors.black12,
                          borderRadius: BorderRadius.circular(20),
                          ),
                          alignment: Alignment.center,
                          child:

                          Text(
                                "15", 
                                style: TextStyle(
                                  color: TColor.primaryTextW,
                                  fontSize: 14,
                                  fontWeight: FontWeight.w700,),

                                ),
                                ),
                          ],),
                         ),
             ),


             const SizedBox(
              height: 25,
            ),

            ],
          ),
        )




    ],
  ),
  );
}

void addMarker() async {
  await controller.setMarkerOfStaticPoint(
    id: "pickup", 
    markerIcon: MarkerIcon(
    iconWidget: Image.asset(
      "assets/img/pickup_pin.png", 
      width: 80, 
      height: 80,
      ),
  ),);

   await controller.setMarkerOfStaticPoint(
    id: "dropoff", 
    markerIcon: MarkerIcon(
    iconWidget: Image.asset(
      "assets/img/drop_pin.png", 
      width: 80, 
      height: 80,
      ),
  ),);


  await controller.setStaticPosition( 
    [GeoPoint(latitude: 28.0398257, longitude: 82.4838780)], 
    "pickup");

    await controller.setStaticPosition( 
    [GeoPoint(latitude: 28.0403014, longitude: 82.4838194)], 
    "dropoff");

loadMapRoad();
}
void loadMapRoad() async {
await controller.drawRoad( 
  GeoPoint(latitude: 28.0398257, longitude: 82.4838780), 
  GeoPoint(latitude: 28.0403014, longitude: 82.4838194), 
  roadType: RoadType.car,
  roadOption: 
  const RoadOption(roadColor: Colors.blueAccent, roadBorderWidth: 3)
  );
}

  @override
  Future<void> mapIsReady(bool isReady) async{
    if(isReady) {
      addMarker();
    }

  }
}
